/**
 * PDF Builder React Wrapper
 * Interface entre WordPress et React
 */

(function() {
  'use strict';

  console.log('[PDF Builder] React Wrapper initializing');

  window.pdfBuilderReactWrapper = {
    init: function(rootElement, config) {
      console.log('[PDF Builder] React Wrapper initialized with config:', config);
      return true;
    },
    render: function(component) {
      console.log('[PDF Builder] Rendering component');
      return true;
    }
  };

  // Rendre compatible avec les anciens appels
  window.initPDFBuilderReact = function(config) {
    console.log('[PDF Builder] initPDFBuilderReact called');
    return window.pdfBuilderReactWrapper.init(document.getElementById('pdf-builder-root'), config);
  };

  console.log('[PDF Builder] React Wrapper loaded');
})();
