/**
 * PDF Builder React Main Bundle
 * Application React pour l'éditeur PDF
 */

(function() {
  'use strict';

  console.log('[PDF Builder] React main bundle initializing');

  // Vérifier que le wrapper est chargé
  if (!window.pdfBuilderReact) {
    console.error('[PDF Builder] React wrapper not found');
    return;
  }

  // Vérifier les dépendances React
  if (typeof window.React === 'undefined') {
    console.error('[PDF Builder] React not loaded');
    return;
  }

  if (typeof window.ReactDOM === 'undefined') {
    console.error('[PDF Builder] ReactDOM not loaded');
    return;
  }

  console.log('[PDF Builder] React bundle loaded successfully');
  console.log('[PDF Builder] Dependencies verified');

  // Signaler que le bundle est prêt
  window.pdfBuilderReactBundleReady = true;

})();