    /**
     * Initialise les hooks WordPress
     */
    private function initHooks()
    {
        // Enregistrer le custom post type pour les templates
        add_action('init', [$this, 'register_template_post_type']);

        // Hooks de base de l'admin (restent dans cette classe)
        add_action('admin_menu', [$this, 'addAdminMenu']);
        add_action('admin_enqueue_scripts', [$this, 'enqueue_admin_scripts'], 20);

        // Inclure le gestionnaire de mod√®les pr√©d√©finis
        include_once plugin_dir_path(dirname(__FILE__)) . '../templates/admin/predefined-templates-manager.php';

        // Instancier le gestionnaire de mod√®les pr√©d√©finis
        // new PDF_Builder_Predefined_Templates_Manager();

        // Enregistrer les hooks AJAX du Template_Manager avant m√™me son instantiation
        // Cela garantit que les handlers AJAX seront disponibles imm√©diatement
        add_action('wp_ajax_pdf_builder_save_template', [$this, 'ajax_save_template']);
        add_action('wp_ajax_pdf_builder_pro_save_template', [$this, 'ajax_save_template']);
        add_action('wp_ajax_pdf_builder_get_template', [$this, 'ajax_get_template']);

        // Auto-save handler AJAX - impl√©mentation compl√®te inline pour √©viter les probl√®mes
        add_action('wp_ajax_pdf_builder_auto_save_template', function () {
            try {
                // V√©rifier les permissions
                if (!\current_user_can('manage_options')) {
                    \wp_send_json_error('Permissions insuffisantes');
                }

                // V√©rifier le nonce
                $nonce_valid = false;
                if (isset($_POST['nonce'])) {
                    $nonce_valid = \wp_verify_nonce($_POST['nonce'], 'pdf_builder_nonce') ||
                                  \wp_verify_nonce($_POST['nonce'], 'pdf_builder_order_actions') ||
                                  \wp_verify_nonce($_POST['nonce'], 'pdf_builder_templates');
                }

                if (!$nonce_valid) {
                    \wp_send_json_error('S√©curit√©: Nonce invalide');
                }

                // R√©cup√©ration des donn√©es - Accepter les deux entiers et cha√Ænes
                $template_id_raw = isset($_POST['template_id']) ? $_POST['template_id'] : '';
                $elements = isset($_POST['elements']) ? \wp_unslash($_POST['elements']) : '[]';

                if (empty($template_id_raw)) {
                    \wp_send_json_error('ID template manquant');
                    return;
                }

                // Validation du JSON
                $elements_decoded = \json_decode($elements, true);
                if (\json_last_error() !== JSON_ERROR_NONE) {
                    \wp_send_json_error('JSON invalide: ' . \json_last_error_msg());
                    return;
                }

                // Traiter comme template utilisateur en base de donn√©es
                $template_id = \intval($template_id_raw);
                if (!$template_id) {
                    \wp_send_json_error('ID template invalide');
                    return;
                }

                // Sauvegarde en base de donn√©es
                global $wpdb;
                $table_templates = $wpdb->prefix . 'pdf_builder_templates';

                // R√©cup√©rer le template existant
                $template = $wpdb->get_row(
                    $wpdb->prepare("SELECT * FROM $table_templates WHERE id = %d", $template_id),
                    ARRAY_A
                );

                if (!$template) {
                    \wp_send_json_error('Template non trouv√©');
                    return;
                }

                // D√©coder les donn√©es existantes
                $template_data = \json_decode($template['template_data'], true);
                if ($template_data === null) {
                    $template_data = [];
                }

                // Mettre √† jour les √©l√©ments
                $template_data['elements'] = $elements_decoded;

                // Sauvegarder en base
                $result = $wpdb->update(
                    $table_templates,
                    array(
                        'template_data' => \wp_json_encode($template_data),
                        'updated_at' => \current_time('mysql')
                    ),
                    array('id' => $template_id),
                    array('%s', '%s'),
                    array('%d')
                );

                if ($result === false) {
                    \wp_send_json_error('Erreur de sauvegarde en base de donn√©es');
                    return;
                }

                // R√©ponse de succ√®s
                \wp_send_json_success(array(
                    'message' => 'Auto-save r√©ussi',
                    'template_id' => $template_id,
                    'saved_at' => \current_time('mysql'),
                    'element_count' => \count($elements_decoded)
                ));
            } catch (\Exception $e) {
                \wp_send_json_error('Erreur lors de l\'auto-save: ' . $e->getMessage());
            }
        });

        add_action('wp_ajax_pdf_builder_load_template', [$this, 'ajax_load_template']);
        add_action('wp_ajax_pdf_builder_get_template', [$this, 'ajax_get_template']);
        add_action('wp_ajax_pdf_builder_flush_rest_cache', [$this, 'ajax_flush_rest_cache']);

        // Hooks WooCommerce - D√©l√©gation vers le manager
        if (class_exists('WooCommerce')) {
            add_action('add_meta_boxes_shop_order', [$this->woocommerce_integration, 'add_woocommerce_order_meta_box']);
            // Le hook HPOS peut ne pas exister dans toutes les versions, on l'enregistre seulement s'il existe
            if (has_action('add_meta_boxes_woocommerce_page_wc-orders') !== false || defined('WC_VERSION') && version_compare(WC_VERSION, '7.1', '>=')) {
                add_action('add_meta_boxes_woocommerce_page_wc-orders', [$this->woocommerce_integration, 'add_woocommerce_order_meta_box']);
            }
        }

        // Hook AJAX pour sauvegarder les param√®tres
        add_action('wp_ajax_pdf_builder_save_settings', [$this, 'ajax_save_settings']);
        add_action('wp_ajax_pdf_builder_save_settings_page', [$this, 'ajax_save_settings_page']);
        add_action('wp_ajax_pdf_builder_save_general_settings', [$this, 'ajax_save_general_settings']);
        add_action('wp_ajax_pdf_builder_save_performance_settings', [$this, 'ajax_save_performance_settings']);
        // Hook AJAX pour migrer les templates obsol√®tes
        add_action('wp_ajax_pdf_builder_migrate_templates', [$this, 'ajax_migrate_templates']);
        add_action('wp_ajax_pdf_builder_migrate_templates_to_posts', [$this, 'ajax_migrate_templates_to_posts']);
        // Hook AJAX pour toggle debug mode
        add_action('wp_ajax_pdf_builder_toggle_debug', [$this, 'ajax_toggle_debug']);
        // Hook AJAX pour toggle debug mode principal
        add_action('wp_ajax_pdf_builder_toggle_debug_mode', [$this, 'ajax_toggle_debug_mode']);

        // Test notifications handler
        add_action('wp_ajax_pdf_builder_test_notifications', [$this, 'ajax_test_notifications']);
        // Test SMTP connection handler
        add_action('wp_ajax_pdf_builder_test_smtp_connection', [$this, 'ajax_test_smtp_connection']);

        // Hook pour la compatibilit√© avec les anciens liens template_id
        add_action('admin_init', [$this, 'handle_legacy_template_links']);
    }

