    /**
     * G√©n√®re un PDF depuis les donn√©es du template
     */
    private function generatePdfFromTemplateData($template, $filename)
    {
        // Cr√©er le r√©pertoire de stockage s'il n'existe pas
        $upload_dir = wp_upload_dir();
        $pdf_dir = $upload_dir['basedir'] . '/pdf-builder';
        if (!file_exists($pdf_dir)) {
            wp_mkdir_p($pdf_dir);
        }

        $pdf_path = $pdf_dir . '/' . $filename;
// G√©n√©rer le PDF avec notre g√©n√©rateur personnalis√©
        // Ici nous utilisons Dompdf pour la g√©n√©ration PDF

        // G√©n√©rer le HTML d'abord
        $html_content = $this->generateUnifiedHtml($template);
// Utiliser notre g√©n√©rateur PDF personnalis√©
        $generator = new PDF_Generator();
        $pdf_content = $generator->generate_from_elements($this->convertTemplateToElements($template));
        if ($pdf_content) {
        // Sauvegarder le contenu HTML/PDF
            file_put_contents($pdf_path, $pdf_content);
            return $pdf_path;
        } else {
            throw new Exception('Erreur lors de la g√©n√©ration du PDF');
        }
    }

    /**
     * Convertit les donn√©es template en format √©l√©ments pour le g√©n√©rateur PDF
     */
    private function convertTemplateToElements($template)
