                        } else {
            // Exemple fictif comme dans le builder
                            $customer_html = '<div style="padding: 8px; font-size: 12px; line-height: 1.4;">';
                            $customer_html .= '<div style="font-weight: bold; margin-bottom: 4px;">Client</div>';
