            $row_count++;
        }

        // Ajouter les frais de commande personnalis√©s
        foreach ($order->get_fees() as $fee) {
            $fee_name = $fee->get_name();
            $fee_total = $fee->get_total();
            $row_style = ($row_count % 2 == 1) ? $alt_row_style_css : $cell_style_css;
            $html .= '<tr>';
            if ($columns['image'] ?? false) {
                $html .= '<td style="' . $row_style . '"></td>';
            }
            if ($columns['name'] ?? true) {
                $html .= '<td style="' . $row_style . ' font-weight: bold;">' . esc_html($fee_name) . '</td>';
            }
            if ($columns['sku'] ?? false) {
                $html .= '<td style="' . $row_style . '"></td>';
            }
            if ($columns['quantity'] ?? true) {
                $html .= '<td style="' . $row_style . ' text-align: center;">-</td>';
            }
            if ($columns['price'] ?? true) {
                $html .= '<td style="' . $row_style . ' text-align: right;">-</td>';
            }
            if ($columns['total'] ?? true) {
                $html .= '<td style="' . $row_style . ' text-align: right; font-weight: bold;">' . wc_price($fee_total) . '</td>';
            }
            $html .= '</tr>';
            $row_count++;
        }

        // Ajouter les lignes de totaux si demand√©es
        if ($show_subtotal) {
            $row_style = $cell_style_css;
            $html .= '<tr>';
            $colspan = 0;
            if ($columns['image'] ?? false) {
                $colspan++;
            }
            if ($columns['name'] ?? true) {
                $colspan++;
            }
            if ($columns['sku'] ?? false) {
                $colspan++;
            }
            if ($columns['quantity'] ?? true) {
                $colspan++;
            }
            if ($columns['price'] ?? true) {
                $colspan++;
            }

            if ($colspan > 0) {
                $html .= '<td colspan="' . $colspan . '" style="' . $row_style . ' text-align: right; font-weight: bold;">Sous-total:</td>';
            }
            if ($columns['total'] ?? true) {
                $html .= '<td style="' . $row_style . ' text-align: right; font-weight: bold;">' . wc_price($order->get_subtotal()) . '</td>';
            }
            $html .= '</tr>';
        }

        $html .= '</tbody></table>';
        return $html;
    }

    /**
     * Transforme les √©l√©ments du format PHP vers le format React
     */
    private function transformElementsForReact($elements)
    {
        if (!is_array($elements)) {
            return [];
        }

        $transformed_elements = [];
        foreach ($elements as $element) {
            $transformed_element = [];

            // Copier les propri√©t√©s de base
            if (isset($element['id'])) {
                $transformed_element['id'] = $element['id'];
            }
            if (isset($element['type'])) {
                $transformed_element['type'] = $element['type'];
            }
            if (isset($element['content'])) {
                $transformed_element['content'] = $element['content'];
            }

            // Transformer position -> x, y
            if (isset($element['position']['x'])) {
                $transformed_element['x'] = (int)$element['position']['x'];
            }
            if (isset($element['position']['y'])) {
                $transformed_element['y'] = (int)$element['position']['y'];
            }

            // Transformer size -> width, height
            if (isset($element['size']['width'])) {
                $transformed_element['width'] = (int)$element['size']['width'];
            }
            if (isset($element['size']['height'])) {
                $transformed_element['height'] = (int)$element['size']['height'];
            }

            // Transformer style -> propri√©t√©s √† plat
            if (isset($element['style'])) {
                if (isset($element['style']['fontSize'])) {
                    $transformed_element['fontSize'] = (int)$element['style']['fontSize'];
                }
                if (isset($element['style']['fontWeight'])) {
                    $transformed_element['fontWeight'] = $element['style']['fontWeight'];
                }
                if (isset($element['style']['color'])) {
                    $transformed_element['color'] = $element['style']['color'];
                }
                if (isset($element['style']['textAlign'])) {
                    $transformed_element['textAlign'] = $element['style']['textAlign'];
                }
                if (isset($element['style']['verticalAlign'])) {
                    $transformed_element['verticalAlign'] = $element['style']['verticalAlign'];
                }
                if (isset($element['style']['backgroundColor'])) {
                    $transformed_element['backgroundColor'] = $element['style']['backgroundColor'];
                }
            }

            // Pour les √©l√©ments text, utiliser content comme text
            if ($element['type'] === 'text' && isset($element['content'])) {
                $transformed_element['text'] = $element['content'];
            }

            // Propri√©t√©s par d√©faut pour tous les √©l√©ments
            if (!isset($transformed_element['x'])) {
                $transformed_element['x'] = 0;
            }
            if (!isset($transformed_element['y'])) {
                $transformed_element['y'] = 0;
            }
            if (!isset($transformed_element['width'])) {
                $transformed_element['width'] = 100;
            }
            if (!isset($transformed_element['height'])) {
                $transformed_element['height'] = 50;
            }
            if (!isset($transformed_element['visible'])) {
                $transformed_element['visible'] = true;
            }
            if (!isset($transformed_element['locked'])) {
                $transformed_element['locked'] = false;
            }

            $transformed_elements[] = $transformed_element;
        }

        return $transformed_elements;
    }

