    /**
     * AJAX handler pour tester la connexion SMTP
     */
    public function ajaxTestSmtpConnection()
    {
        try {
            // V√©rifier les permissions
            if (!current_user_can('manage_options')) {
                wp_send_json_error(['message' => __('Permissions insuffisantes', 'pdf-builder-pro')]);
                return;
            }

            // V√©rifier le nonce
            if (!wp_verify_nonce($_POST['nonce'] ?? '', 'pdf_builder_settings')) {
                wp_send_json_error(['message' => __('Nonce de s√©curit√© invalide', 'pdf-builder-pro')]);
                return;
            }

            // V√©rifier si SMTP est activ√©
            if (!get_option('pdf_builder_smtp_enabled', false)) {
                wp_send_json_error(['message' => __('SMTP n\'est pas activ√©', 'pdf-builder-pro')]);
                return;
            }

            // R√©cup√©rer les param√®tres SMTP
            $smtp_host = get_option('pdf_builder_smtp_host', '');
            $smtp_port = get_option('pdf_builder_smtp_port', 587);
            $smtp_encryption = get_option('pdf_builder_smtp_encryption', 'tls');
            $smtp_username = get_option('pdf_builder_smtp_username', '');
            $smtp_password = get_option('pdf_builder_smtp_password', '');

            // V√©rifier que les param√®tres requis sont pr√©sents
            if (empty($smtp_host) || empty($smtp_username) || empty($smtp_password)) {
                wp_send_json_error(['message' => __('Param√®tres SMTP incomplets', 'pdf-builder-pro')]);
                return;
            }

            // Tester la connexion SMTP avec les commandes SMTP basiques
            $debug_info = [];
            $connection = @fsockopen(
                ($smtp_encryption === 'ssl' ? 'ssl://' : '') . $smtp_host,
                $smtp_port,
                $errno,
                $errstr,
                10 // timeout 10 secondes
            );

            if ($connection) {
                $debug_info[] = "Connected to {$smtp_host}:{$smtp_port} (ssl_prefix=" . ($smtp_encryption === 'ssl' ? 'yes' : 'no') . ")";

                // Lire la r√©ponse initiale
                $response = fgets($connection, 1024);
                $debug_info[] = "Server greeting: " . trim($response);

                // V√©rifier si la r√©ponse contient "220"
                if (strpos($response, '220') !== 0) {
                    fclose($connection);
                    wp_send_json_error([
                        'message' => __('Serveur SMTP ne r√©pond pas correctement. R√©ponse: ', 'pdf-builder-pro') . trim($response),
                        'debug' => $debug_info
                    ]);
                    return;
                }

                // Envoyer EHLO
                fwrite($connection, "EHLO " . ($_SERVER['SERVER_NAME'] ?? 'localhost') . "\r\n");
                $ehlo_response = '';
                while (($line = fgets($connection, 1024)) !== false) {
                    $ehlo_response .= $line;
                    $debug_info[] = "EHLO: " . trim($line);
                    if (isset($line[3]) && $line[3] === ' ') {
                        break; // Fin de la r√©ponse multi-ligne
                    }
                }

                // V√©rifier que EHLO a retourn√© 250
                if (strpos($ehlo_response, '250') === false) {
                    fclose($connection);
                    wp_send_json_error([
                        'message' => __('Commande EHLO rejet√©e par le serveur SMTP', 'pdf-builder-pro'),
                        'debug' => $debug_info
                    ]);
                    return;
                }

                // Si le mode demand√© est TLS (STARTTLS), demander STARTTLS puis activer crypto
                if ($smtp_encryption === 'tls') {
                    // V√©rifier si le serveur annonce STARTTLS
                    if (stripos($ehlo_response, 'STARTTLS') === false) {
                        fclose($connection);
                        wp_send_json_error([
                            'message' => __('Le serveur SMTP ne supporte pas STARTTLS, v√©rifiez le port / chiffrement.', 'pdf-builder-pro'),
                            'debug' => $debug_info
                        ]);
                        return;
                    }

                    // Envoyer STARTTLS
                    fwrite($connection, "STARTTLS\r\n");
                    $starttls_response = fgets($connection, 1024);
                    $debug_info[] = "STARTTLS response: " . trim($starttls_response);

                    if (strpos($starttls_response, '220') !== 0) {
                        fclose($connection);
                        wp_send_json_error([
                            'message' => __('√âchec de la n√©gociation STARTTLS. R√©ponse: ', 'pdf-builder-pro') . trim($starttls_response),
                            'debug' => $debug_info
                        ]);
                        return;
                    }

                    // Activer TLS
                    $crypto_ok = @stream_socket_enable_crypto($connection, true, STREAM_CRYPTO_METHOD_TLS_CLIENT);
                    $debug_info[] = 'stream_socket_enable_crypto result: ' . ($crypto_ok ? 'ok' : 'failed');

                    if (!$crypto_ok) {
                        fclose($connection);
                        wp_send_json_error([
                            'message' => __('√âchec de l\'activation du chiffrement TLS (STARTTLS). V√©rifiez la configuration OpenSSL/port.', 'pdf-builder-pro'),
                            'debug' => $debug_info
                        ]);
                        return;
                    }

                    // R√©-envoyer EHLO apr√®s STARTTLS
                    fwrite($connection, "EHLO " . ($_SERVER['SERVER_NAME'] ?? 'localhost') . "\r\n");
                    $ehlo_response = '';
                    while (($line = fgets($connection, 1024)) !== false) {
                        $ehlo_response .= $line;
                        $debug_info[] = "EHLO (post-STARTTLS): " . trim($line);
                        if (isset($line[3]) && $line[3] === ' ') {
                            break;
                        }
                    }
                }

                // Tester l'authentification si n√©cessaire
                if (!empty($smtp_username) && !empty($smtp_password)) {
                    // Envoyer AUTH LOGIN
                    fwrite($connection, "AUTH LOGIN\r\n");
                    $response = fgets($connection, 1024);
                    $debug_info[] = "AUTH LOGIN response: " . trim($response);

                    if (strpos($response, '334') !== 0) {
                        fclose($connection);
                        wp_send_json_error([
                            'message' => __('Authentification SMTP non support√©e par le serveur. R√©ponse: ', 'pdf-builder-pro') . trim($response),
                            'debug' => $debug_info
                        ]);
                        return;
                    }

                    // Envoyer le nom d'utilisateur encod√© en base64
                    fwrite($connection, base64_encode($smtp_username) . "\r\n");
                    $response = fgets($connection, 1024);
                    $debug_info[] = "Username response: " . trim($response);

                    if (strpos($response, '334') !== 0) {
                        fclose($connection);
                        wp_send_json_error([
                            'message' => __('Nom d\'utilisateur SMTP invalide. R√©ponse: ', 'pdf-builder-pro') . trim($response),
                            'debug' => $debug_info
                        ]);
                        return;
                    }

                    // Envoyer le mot de passe encod√© en base64
                    fwrite($connection, base64_encode($smtp_password) . "\r\n");
                    $response = fgets($connection, 1024);
                    $debug_info[] = "Password response: " . trim($response);

                    if (strpos($response, '235') !== 0) {
                        fclose($connection);
                        wp_send_json_error([
                            'message' => __('Mot de passe SMTP invalide. R√©ponse: ', 'pdf-builder-pro') . trim($response),
                            'debug' => $debug_info
                        ]);
                        return;
                    }
                }

                // Fermer proprement la connexion
                fwrite($connection, "QUIT\r\n");
                $quit_response = fgets($connection, 1024);
                $debug_info[] = "QUIT response: " . trim($quit_response);
                fclose($connection);

                wp_send_json_success([
                    'message' => __('Connexion et authentification SMTP r√©ussies', 'pdf-builder-pro'),
                    'debug' => $debug_info
                ]);
            } else {
                wp_send_json_error([
                    'message' => __('Impossible de se connecter au serveur SMTP : ', 'pdf-builder-pro') . $errstr,
                    'debug' => ["fsockopen error: {$errno} - {$errstr}"]
                ]);
            }
        } catch (Exception $e) {
            wp_send_json_error([
                'message' => __('Erreur lors du test : ', 'pdf-builder-pro') . $e->getMessage()
            ]);
        }
    }

    /**
     * Parse SQL statements from a string
     *
     * @param  string $sql SQL content to parse
     * @return array<int, string> Array of SQL statements
     */
    private function parseSqlStatements($sql)
    {
        $statements = [];
        $current_statement = '';
        $lines = explode("\n", $sql);
        foreach ($lines as $line) {
        // Ignorer les commentaires
            if (strpos(trim($line), '--') === 0) {
                continue;
            }

            $current_statement .= $line . "\n";
        // V√©rifier si la ligne se termine par un point-virgule
            if (substr(trim($line), -1) === ';') {
                $statements[] = trim($current_statement);
                $current_statement = '';
            }
        }

        // Ajouter la derni√®re d√©claration si elle n'est pas vide
        if (!empty(trim($current_statement))) {
            $statements[] = trim($current_statement);
        }

        return $statements;
    }

