    /**
     * G√©n√®re un PDF pour une commande WooCommerce (m√©thode publique pour l'int√©gration)
     *
     * @param  int $order_id    ID de la commande
     * @param  int $template_id ID du template (0 pour auto-d√©tection)
     * @return mixed URL du PDF g√©n√©r√© ou erreur
     */
    public function generate_order_pdf($order_id, $template_id = 0)
    {
        // D√©sactiver l'affichage des erreurs PHP pour √©viter les r√©ponses HTML
        if (!defined('WP_DEBUG') || !WP_DEBUG) {
            ini_set('display_errors', 0);
            error_reporting(0);
        }


        // V√©rifier que WooCommerce est actif
        if (!class_exists('WooCommerce')) {
            return new WP_Error('woocommerce_missing', 'WooCommerce n\'est pas install√© ou activ√©');
        }

        // V√©rifier que les fonctions WooCommerce n√©cessaires existent
        if (!function_exists('wc_get_order')) {
            return new WP_Error('woocommerce_functions_missing', 'Fonction wc_get_order non disponible - WooCommerce mal install√©');
        }

        $order = wc_get_order($order_id);
        if (!$order) {
            return new WP_Error('order_not_found', 'Commande non trouv√©e');
        }


        // V√©rifier que l'objet order a les m√©thodes n√©cessaires
        if (!method_exists($order, 'get_id') || !method_exists($order, 'get_total')) {
            return new WP_Error('invalid_order_object', 'Objet commande WooCommerce invalide');
        }

        try {
// Charger le template de mani√®re robuste
            if ($template_id > 0) {
                $template_data = $this->load_template_robust($template_id);
            } else {
            // V√©rifier s'il y a un template sp√©cifique pour le statut de la commande
                $order_status = $order->get_status();
                $status_templates = get_option('pdf_builder_order_status_templates', []);
                $status_key = 'wc-' . $order_status;
                if (isset($status_templates[$status_key]) && $status_templates[$status_key] > 0) {
                    $mapped_template_id = $status_templates[$status_key];
                    $template_data = $this->load_template_robust($mapped_template_id);
                } else {
                    $template_data = $this->get_default_invoice_template();
                }
            }

            if (!$template_data) {
                return new WP_Error('template_load_failed', 'Impossible de charger le template');
            }

            // G√©n√©rer le PDF avec les donn√©es de la commande
            $pdf_filename = 'order-' . $order_id . '-' . time() . '.pdf';
            $pdf_path = $this->generate_order_pdf_private($order, $template_data, $pdf_filename);
            if ($pdf_path && file_exists($pdf_path)) {
                $upload_dir = wp_upload_dir();
                $pdf_url = str_replace($upload_dir['basedir'], $upload_dir['baseurl'], $pdf_path);
                return $pdf_url;
            } else {
                return new WP_Error('pdf_generation_failed', 'Erreur lors de la g√©n√©ration du PDF - fichier non cr√©√©');
            }
        } catch (Exception $e) {
            return new WP_Error('generation_exception', 'Erreur inconnue lors de la g√©n√©ration: ' . $e->getMessage());
        } catch (Error $e) {
            return new WP_Error('generation_error', 'Erreur fatale lors de la g√©n√©ration: ' . $e->getMessage());
        }
    }

    /**
     * Remplacer les variables WooCommerce dans les donn√©es du template
     *
     * @param  array $template_data    Donn√©es du
     *                                 template
     * @param  array $woocommerce_data Donn√©es WooCommerce
     * @return array Template avec variables remplac√©es
     */
    private function replace_woocommerce_variables($template_data, $woocommerce_data)
    {
        $processed_data = $template_data;
// Fonction r√©cursive pour remplacer les variables dans toutes les profondeurs
        $replace_vars = function ($data) use ($woocommerce_data, &$replace_vars) {

            if (is_array($data)) {
                $result = [];
                foreach ($data as $key => $value) {
                    $result[$key] = $replace_vars($value);
                }
                return $result;
            } elseif (is_string($data)) {
            // Remplacer les variables du type {order_number}, {customer_name}, etc.
                $replaced = $data;
                foreach ($woocommerce_data as $var => $value) {
                    $replaced = str_replace('{' . $var . '}', $value, $replaced);
                }
                return $replaced;
            } else {
                return $data;
            }
        };
        return $replace_vars($processed_data);
    }

    private function load_template_by_id($template_id)
    {
        global $wpdb;
        $table_templates = $wpdb->prefix . 'pdf_builder_templates';
        $template_data = $wpdb->get_var($wpdb->prepare("SELECT template_data FROM $table_templates WHERE id = %d", $template_id));
        if (!$template_data) {
            return null;
        }

        $template = json_decode($template_data, true);
// MIGRATION: Corriger les valeurs par d√©faut obsol√®tes dans les templates
        // Les templates cr√©√©s avant cette correction peuvent avoir des valeurs par d√©faut incorrectes
        if (is_array($template) && isset($template['elements']) && is_array($template['elements'])) {
            foreach ($template['elements'] as &$element) {
                if ($element['type'] === 'product_table') {
        // Valeurs par d√©faut correctes du Canvas Elements Manager
                    // showSubtotal, showDiscount, showTotal doivent √™tre false par d√©faut
                    // showShipping et showTaxes doivent √™tre true par d√©faut

                    // G√©n√©rer les headers automatiquement s'ils ne correspondent pas aux colonnes
                    $columns = $element['columns'] ?? [];
                    $headers = $element['headers'] ?? [];
                    $visible_count = count(array_filter($columns));
                    if (is_array($headers) && count($headers) !== $visible_count) {
                            // Les headers ne correspondent pas au nombre de colonnes visibles
                        // Les r√©g√©n√©rer automatiquement
                        $default_headers_map = [
                            'image' => 'Image',
                            'name' => 'Produit',
                            'sku' => 'SKU',
                            'quantity' => 'Qt√©',
                            'price' => 'Prix',
                            'total' => 'Total'
                        ];
                            $element['headers'] = [];
                        foreach ($columns as $col_name => $col_visible) {
                            if ($col_visible) {
                                        $element['headers'][] = $default_headers_map[$col_name] ?? ucfirst($col_name);
                            }
                        }

                    }
                }
            }
        }

        return $template;
    }

    /**
     * D√©tection automatique du template bas√© sur le statut de commande
     */
    private function auto_detect_template($order)
    {
        $order_status = $order->get_status();
        $status_templates = get_option('pdf_builder_order_status_templates', []);
        $status_key = 'wc-' . $order_status;
        if (isset($status_templates[$status_key]) && $status_templates[$status_key] > 0) {
            $template_id = $status_templates[$status_key];
            $template = $this->load_template_by_id($template_id);
            return $template;
        }

        // Template par d√©faut si aucun trouv√©
        global $wpdb;
        $table_templates = $wpdb->prefix . 'pdf_builder_templates';
        $default_template = $wpdb->get_var("SELECT template_data FROM $table_templates ORDER BY id ASC LIMIT 1");
        if ($default_template) {
            return json_decode($default_template, true);
        }

        return null;
    }

    /**
     * AJAX pour migrer les templates obsol√®tes aux nouvelles valeurs par d√©faut
     * Appel√© via /wp-admin/admin-ajax.php?action=pdf_builder_migrate_templates
     */
    public function ajax_migrate_templates()
    {
        // V√©rifier les permissions
        if (!current_user_can('manage_options')) {
            wp_send_json_error(['message' => 'Permissions insuffisantes']);
            return;
        }

        global $wpdb;
        $table = $wpdb->prefix . 'pdf_builder_templates';
        try {
        // R√©cup√©rer tous les templates
            $templates = $wpdb->get_results("SELECT id, template_data FROM $table");
            if (empty($templates)) {
                wp_send_json_success(['message' => 'Aucun template √† migrer', 'migrated' => 0, 'total' => 0]);
                return;
            }

            $migrated_count = 0;
            $errors = [];
            foreach ($templates as $row) {
                $template = json_decode($row->template_data, true);
                if (!is_array($template) || empty($template['elements'])) {
                    continue;
                }

                $template_modified = false;
    // Migrer chaque √©l√©ment product_table
                foreach ($template['elements'] as &$element) {
                    if ($element['type'] === 'product_table') {
                    // V√©rifier et corriger les headers
                        $columns = $element['columns'] ?? [];
                        $headers = $element['headers'] ?? [];
                        $visible_count = count(array_filter($columns));
                        if (!is_array($headers) || count($headers) !== $visible_count) {
                // R√©g√©n√©rer les headers
                            $default_headers_map = [
                                'image' => 'Image',
                                'name' => 'Produit',
                                'sku' => 'SKU',
                                'quantity' => 'Qt√©',
                                'price' => 'Prix',
                                'total' => 'Total'
                            ];
                            $element['headers'] = [];
                            foreach ($columns as $col_name => $col_visible) {
                                if ($col_visible) {
                                    $element['headers'][] = $default_headers_map[$col_name] ?? ucfirst($col_name);
                                }
                            }

                            $template_modified = true;
                        }
                    }
                }

            // Sauvegarder si modifi√©
                if ($template_modified) {
                    $updated = $wpdb->update($table, ['template_data' => json_encode($template)], ['id' => $row->id], ['%s'], ['%d']);
                    if ($updated !== false) {
                        $migrated_count++;
                    } else {
                        $errors[] = "Erreur lors de la migration du template ID={$row->id}";
                    }
                }
            }

            wp_send_json_success([
                'message' => "Migration termin√©e: $migrated_count template(s) corrig√©(s)",
                'migrated' => $migrated_count,
                'total' => count($templates),
                'errors' => $errors
                ]);
        } catch (Exception $e) {
            wp_send_json_error(['message' => 'Erreur lors de la migration: ' . $e->getMessage()]);
        }
    }

    /**
     * Parse SQL statements from a string
     *
     * @param  string $sql SQL content to parse
     * @return array<int, string> Array of SQL statements
     */
    private function parse_sql_statements($sql)
    {
        $statements = [];
        $current_statement = '';
        $lines = explode("\n", $sql);
        foreach ($lines as $line) {
        // Ignorer les commentaires
            if (strpos(trim($line), '--') === 0) {
                continue;
            }

            $current_statement .= $line . "\n";
        // V√©rifier si la ligne se termine par un point-virgule
            if (substr(trim($line), -1) === ';') {
                $statements[] = trim($current_statement);
                $current_statement = '';
            }
        }

        // Ajouter la derni√®re d√©claration si elle n'est pas vide
        if (!empty(trim($current_statement))) {
            $statements[] = trim($current_statement);
        }

        return $statements;
    }

