        if (is_array($elements)) {
// Trier les √©l√©ments par position Y puis X pour un meilleur rendu
            usort($elements, function ($a, $b) {

                    $a_y = $a['position']['y'] ?? $a['y'] ?? 0;
                $b_y = $b['position']['y'] ?? $b['y'] ?? 0;
