            // V√©rifier si le template existe d√©j√†
            $existing = $wpdb->get_var($wpdb->prepare("SELECT id FROM $table_templates WHERE id = %d", $template_id));

            if ($existing) {
                // Mettre √† jour le template existant
                $wpdb->update(
