        }

        return $default_template;
    }

    /**
     * R√©cup√®re les statistiques pour le tableau de bord
     */
    private function getDashboardStats()
    {
        global $wpdb;

        // Nombre de templates
        $table_templates = $wpdb->prefix . 'pdf_builder_templates';
        $templates_count = $wpdb->get_var("SELECT COUNT(*) FROM $table_templates");

        // Nombre total de documents g√©n√©r√©s (logs)
        $table_logs = $wpdb->prefix . 'pdf_builder_logs';
        $documents_count = 0;
        $today_count = 0;

        if ($wpdb->get_var("SHOW TABLES LIKE '$table_logs'") == $table_logs) {
            // V√©rifier si la colonne log_message existe
            $columns = $wpdb->get_results("DESCRIBE $table_logs");
            $has_log_message = false;
            foreach ($columns as $column) {
                if ($column->Field === 'log_message') {
                    $has_log_message = true;
                    break;
                }
            }

            if ($has_log_message) {
                $documents_count = $wpdb->get_var("SELECT COUNT(*) FROM $table_logs WHERE log_message LIKE '%PDF g√©n√©r√©%' OR log_message LIKE '%Document cr√©√©%'");

                // Documents g√©n√©r√©s aujourd'hui
                $today = date('Y-m-d');
                $today_count = $wpdb->get_var($wpdb->prepare(
                    "SELECT COUNT(*) FROM $table_logs WHERE DATE(created_at) = %s AND (log_message LIKE '%PDF g√©n√©r√©%' OR log_message LIKE '%Document cr√©√©%')",
                    $today
                ));
            } else {
                // Si la colonne n'existe pas, compter tous les logs
                $documents_count = $wpdb->get_var("SELECT COUNT(*) FROM $table_logs");

                // Documents d'aujourd'hui
                $today = date('Y-m-d');
                $today_count = $wpdb->get_var($wpdb->prepare(
                    "SELECT COUNT(*) FROM $table_logs WHERE DATE(created_at) = %s",
                    $today
                ));
            }
        }

        return [
            'templates' => (int) $templates_count,
            'documents' => (int) $documents_count,
            'today' => (int) $today_count
        ];
    }

