    /**
     * üîß DIAGNOSTIC ET CORRECTION DE L'AUTO-SAVE
     * Force l'activation de l'auto-save si d√©sactiv√©
     */
    public function diagnose_and_fix_autosave()
    {
        // Ne s'ex√©cuter que sur les pages PDF Builder
        if (!isset($_GET['page']) || strpos($_GET['page'], 'pdf-builder') === false) {
            return;
        }

        // V√©rifier et corriger les options d'auto-save
        $auto_save_enabled = get_option('pdf_builder_canvas_auto_save', '1');
        $autosave_enabled = get_option('pdf_builder_canvas_autosave_enabled', '1');

        // Forcer l'activation si d√©sactiv√©
        if ($auto_save_enabled !== '1') {
            update_option('pdf_builder_canvas_auto_save', '1');
            error_log('PDF Builder: Auto-save forc√© √† ON (√©tait: ' . $auto_save_enabled . ')');
        }

        if ($autosave_enabled !== '1') {
            update_option('pdf_builder_canvas_autosave_enabled', '1');
            error_log('PDF Builder: Autosave forc√© √† ON (√©tait: ' . $autosave_enabled . ')');
        }

        // S'assurer que l'intervalle est raisonnable
        $interval = intval(get_option('pdf_builder_canvas_auto_save_interval', 5));
        if ($interval <= 0) {
            update_option('pdf_builder_canvas_auto_save_interval', 5);
            error_log('PDF Builder: Intervalle auto-save corrig√© √† 5 minutes (√©tait: ' . $interval . ')');
        }
    }

    /**
     * üîß MISE √Ä JOUR TEMPORAIRE DES NOMS DE TEMPLATES
     * Met √† jour les noms des templates existants pour √©viter "Template 1"
     */
    public function update_template_names()
    {
        // Ne s'ex√©cuter que pour les admins
        if (!current_user_can('manage_options')) {
            return;
        }

        // Toujours ex√©cuter pour s'assurer que c'est √† jour
        // $update_done = get_option('pdf_builder_template_names_updated', false);
        // if ($update_done) {
        //     return;
        // }

        global $wpdb;
        $table_templates = $wpdb->prefix . 'pdf_builder_templates';

        // R√©cup√©rer tous les templates
        $templates = $wpdb->get_results("SELECT id, name, template_data FROM $table_templates", ARRAY_A);

        $updated_count = 0;
        foreach ($templates as $template) {
            $template_id = $template['id'];
            $current_name = $template['name'];
            $template_data = json_decode($template['template_data'], true);

            // G√©n√©rer un nouveau nom si n√©cessaire
            $new_name = $current_name;
            if (empty($current_name) || $current_name === 'Template ' . $template_id) {
                if (isset($template_data['name']) && !empty($template_data['name'])) {
                    $new_name = $template_data['name'];
                } else {
                    // Essayer de deviner le type de template
                    if (isset($template_data['elements']) && is_array($template_data['elements'])) {
                        $has_invoice_elements = false;
                        $has_quote_elements = false;

                        foreach ($template_data['elements'] as $element) {
                            $content = isset($element['content']) ? strtolower($element['content']) : '';
                            if (strpos($content, 'facture') !== false || strpos($content, 'invoice') !== false) {
                                $has_invoice_elements = true;
                            }
                            if (strpos($content, 'devis') !== false || strpos($content, 'quote') !== false) {
                                $has_quote_elements = true;
                            }
                        }

                        if ($has_invoice_elements) {
                            $new_name = 'Facture ' . $template_id;
                        } elseif ($has_quote_elements) {
                            $new_name = 'Devis ' . $template_id;
                        } else {
                            $new_name = 'Template ' . $template_id;
                        }
                    } else {
                        $new_name = 'Template ' . $template_id;
                    }
                }
            }

            // Mettre √† jour le nom dans la DB si n√©cessaire
            if ($new_name !== $current_name) {
                $wpdb->update(
                    $table_templates,
                    ['name' => $new_name],
                    ['id' => $template_id],
                    ['%s'],
                    ['%d']
                );
                $updated_count++;
                error_log('PDF Builder: Nom du template ' . $template_id . ' mis √† jour: "' . $current_name . '" -> "' . $new_name . '"');
            }

            // S'assurer que le nom est aussi dans les donn√©es JSON
            if (!isset($template_data['name']) || $template_data['name'] !== $new_name) {
                $template_data['name'] = $new_name;
                $updated_json = wp_json_encode($template_data);

                $wpdb->update(
                    $table_templates,
                    ['template_data' => $updated_json],
                    ['id' => $template_id],
                    ['%s'],
                    ['%d']
                );
            }
        }

        if ($updated_count > 0) {
            error_log('PDF Builder: Mise √† jour des noms de templates termin√©e - ' . $updated_count . ' templates mis √† jour');
        }

        // Marquer comme fait (comment√© pour permettre la r√©-ex√©cution)
        // update_option('pdf_builder_template_names_updated', true);
    }
